            double F4_t[5];

            gpu::computeBoysFunctionEriJ(F4_t, S1 * S2 * inv_S4 * r2_PQ, 4, boys_func_table, boys_func_ft);

            const auto QC_0 = (a_l * inv_S2) * (r_l[c0] - r_k[c0]);
