                            delta[d0][d1] * (PQ[b0] * PQ[c0] + PQ[b0] * QC_0)
                            + delta[c0][d1] * (PQ[b0] * PQ[d0] + PQ[b0] * QD_0)
                            + delta[c0][d0] * (PQ[b0] * PQ[d1] + PQ[b0] * QD_1)
                            + delta[b0][d1] * (PQ[c0] * QD_0 + PQ[d0] * QC_0)
