                        + 0.5 * S1 * S1 * inv_S2 * inv_S4 * inv_S4 * (
                            delta[d0][d1] * (PB_0 * PQ[c0])
                            + delta[c0][d1] * (PB_0 * PQ[d0])
                            + delta[c0][d0] * (PB_0 * PQ[d1])
                        )

                    )

                    + F4_t[3] * (

                        (-1.0) * S1 * S1 * S1 * inv_S4 * inv_S4 * inv_S4 * (
                            PB_0 * PQ[c0] * PQ[d0] * PQ[d1]
                        )

                        + S1 * S1 * S2 * inv_S4 * inv_S4 * inv_S4 * (
                            
