            double F2_t[3];

            gpu::computeBoysFunctionEriJ(F2_t, S1 * S2 * inv_S4 * r2_PQ, 2, boys_func_table, boys_func_ft);

            const double eri_ijkl = Lambda * S_ij_00 * S_kl_00 * (

                    F2_t[0] * (

                        (
                            
                            + PB_0 * PA_0
                        )

                        + 0.5 * inv_S1 * (
                            delta[a0][b0]
                        )

