                            + (delta[a0][a1] * delta[b0][b1] * delta[c0][d1] + delta[a0][b0] * delta[a1][b1] * delta[c0][d1] + delta[a0][b1] * delta[a1][b0] * delta[c0][d1]) * (PQ[d0] * 2.0 + QD_0 * 2.0)
