                            + delta[a1][b0] * (PQ[a0] * PQ[b1] * QD_0 * QD_1 * QC_0)
                            + delta[a0][b1] * (PQ[a1] * PQ[b0] * QD_0 * QD_1 * QC_0)
                            + delta[a0][b0] * (PQ[a1] * PQ[b1] * QD_0 * QD_1 * QC_0)
                            + delta[a0][a1] * (PQ[b0] * PQ[b1] * QD_0 * QD_1 * QC_0)
