                            + delta[a1][b0] * delta[c0][d1] * (PB_1 * PQ[a0] * PQ[d0] * (-1.0) + PA_0 * PQ[b1] * PQ[d0] * (-1.0) + PQ[a0] * PQ[b1] * PQ[d0])
                            + (delta[a1][c0] * delta[b0][d1] + delta[a1][d1] * delta[b0][c0]) * (PB_1 * PQ[a0] * PQ[d0] * (-1.0) + PA_0 * PQ[b1] * PQ[d0] * (-1.0))
                            + delta[a1][b0] * delta[c0][d0] * (PB_1 * PQ[a0] * PQ[d1] * (-1.0) + PA_0 * PQ[b1] * PQ[d1] * (-1.0) + PQ[a0] * PQ[b1] * PQ[d1])
                            + (delta[a1][c0] * delta[b0][d0] + delta[a1][d0] * delta[b0][c0]) * (PB_1 * PQ[a0] * PQ[d1] * (-1.0) + PA_0 * PQ[b1] * PQ[d1] * (-1.0))
                            + (delta[a1][b0] * delta[b1][d1] + delta[a1][b1] * delta[b0][d1] + delta[a1][d1] * delta[b0][b1]) * (PA_0 * PQ[c0] * PQ[d0] * (-1.0) + PQ[a0] * PQ[c0] * PQ[d0])
                            + (delta[a1][b0] * delta[b1][d0] + delta[a1][b1] * delta[b0][d0] + delta[a1][d0] * delta[b0][b1]) * (PA_0 * PQ[c0] * PQ[d1] * (-1.0) + PQ[a0] * PQ[c0] * PQ[d1])
