            d_cart_inds[2][0] = 0; d_cart_inds[2][1] = 2;
            d_cart_inds[3][0] = 1; d_cart_inds[3][1] = 1;
            d_cart_inds[4][0] = 1; d_cart_inds[4][1] = 2;
            d_cart_inds[5][0] = 2; d_cart_inds[5][1] = 2;

