    __shared__ double   sum_J_ij[TILE_DIM_LARGE];
    __shared__ uint32_t d_cart_inds[6][2];
    __shared__ double   delta[3][3];

    __shared__ double a_i, a_j, r_i[3], r_j[3], S_ij_00, S1, inv_S1;
