        __syncthreads();

        if (threadIdx.y == 0)
        {
            J_ij = 0.0;

            for (uint32_t n = 0; n < TILE_DIM_LARGE; n++)
            {
                J_ij += sum_J_ij[n];
            }

            mat_J[ij] += J_ij;
        }
