                            + delta[b1][d0] * (PB_0 * PQ[a0] * QC_0 + PA_0 * PQ[b0] * QC_0)
                            + delta[b1][c0] * (PB_0 * PQ[a0] * QD_0 + PA_0 * PQ[b0] * QD_0)
